import "./map.css";
import React, { useRef } from "react";
import "leaflet/dist/leaflet.css";
import { MapContainer, Marker, TileLayer, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import { Icon, divIcon, point } from "leaflet";

export default function Map({ sampleData, focusedCoordinates }) {
  console.log(focusedCoordinates);

  const mapRef = useRef(null);

  const customIcon = new Icon({
    iconUrl: require("./icons/location.png"),
    iconSize: [38, 38],
  });

  const createCustomClustorIcon = (cluster) => {
    return new divIcon({
      html: `<div class="cluster-icon">${cluster.getChildCount()}</div>`,
      className: "custom-marker-cluster",
      iconSize: point(33, 33, true),
    });
  };
  useEffect(() => {
    if (focusedCoordinates) {
      // Check if focusedCoordinates has valid lat and lng values
      if (focusedCoordinates.lat && focusedCoordinates.lng) {
        const { lat, lng } = focusedCoordinates;
        // Use the setView method to focus the map on the provided coordinates
        mapRef.current.setView([lat, lng], 13);
      }
    }
  }, [focusedCoordinates]);
  return (
    <MapContainer center={focusedCoordinates || [22.8566, 2.3225]} zoom={13}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      ></TileLayer>
      <MarkerClusterGroup
        chunkedLoading
        iconCreateFunction={createCustomClustorIcon}
      >
        {sampleData.map((marker) => (
          <Marker position={[marker.lat, marker.lng]} icon={customIcon}>
            <Popup> {marker.name}</Popup>
          </Marker>
        ))}
      </MarkerClusterGroup>
    </MapContainer>
  );
}
