// LocationGrid.js
import React, { useState, useEffect, useMemo } from "react";
import "./LocationGrid.css";
import Map from "./Maps";
import LocationTable from "./LocationTable";
import { postRequest, putRequest, getRequest } from "./helper/ApiRequests";

import ModalForm from "./ModalForm";

const LocationGrid = () => {
  // Sample data for multiple rows

  // const [rows, setRows] = useState([]);

  const [locations, setLocations] = useState([]);
  const url = "http://192.168.1.10:7147/api/Location";
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editLocation, setEditLocation] = useState(null);
  const [isEditFormOpen, setIsEditFormOpen] = useState(false);
  // const getData = async (url) => {
  //   await getRequest(url);
  // };

  useEffect(() => {
    (async () => {
      const data = await getRequest(url);
      setLocations(data);
    })();
  }, []);

  // const toggleForm = () => {
  //   setIsFormOpen(!isFormOpen);
  // };
  const handleAddLocation = async (newLocation) => {
    await postRequest(url, newLocation);
    setLocations([...locations, newLocation]);
    setIsFormOpen(false);
    getRequest(url); // Close the form after adding a location
  };

  const openEditForm = (location) => {
    setEditLocation(location);
    setIsEditFormOpen(true);
  };

  const handleEditLocation = async (newLocation) => {
    console.log(locations[newLocation.name]);
    setEditLocation(newLocation);
    await putRequest(
      `http://192.168.1.10:7147/api/Location/${locations.id}`,
      newLocation
    );
    setIsFormOpen(false);
    getRequest(url);
  };

  // const [formData, setFormData] = useState({
  //   name: "",
  //   lat: "",
  //   lng: "",
  // });

  // const [formErrors, setFormErrors] = useState({
  //   name: "",
  //   lat: "",
  //   lng: "",
  // });

  // const handleInputChange = (e) => {
  //   const { name, value } = e.target;
  //   if (!formData.name || !formData.lat || !formData.lng) {
  //     setFormErrors({ ...formErrors, name: "Name is required" });
  //     return;
  //   }
  //   setFormData({ ...formData, [name]: value });
  // };

  // const handleEditLocation = (index) => {
  //   // Add your logic to open a modal or form to edit location details
  //   // Then, update the location in the locations array
  //   const editedLocation = { ...locations[index], name: "Edited Location" };
  //   const updatedLocations = [...locations];
  //   updatedLocations[index] = editedLocation;
  //   setLocations(updatedLocations);
  // };

  return (
    <div className="container">
      <div className="left">
        <Map sampleData={locations} />
      </div>
      <div className="right">
        <LocationTable
          locations={locations}
          setIsFormOpen={setIsFormOpen}
          setIsEditFormOpen={openEditForm}
        />
      </div>
      {isFormOpen && (
        <ModalForm
          onAddLocation={handleAddLocation}
          setIsFormOpen={setIsFormOpen}
        />
      )}
      {isEditFormOpen && (
        <ModalForm
          locationData={editLocation}
          onAddLocation={handleEditLocation}
          setIsFormOpen={setIsEditFormOpen}
        />
      )}
      {/* <input
        type="text"
        name="name"
        value={formData.name}
        onChange={handleInputChange}
      />
      {formErrors.name && <span className="error">{formErrors.name}</span>} */}
    </div>
  );
};

export default LocationGrid;

