const sendRequest = async (url, body, method) => {
  console.log(body);
  const response = await fetch(url, {
    method,
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      Cache: "no-cache",
    },
    body,
  });

  if (response.status === 200 || response.status === 201) {
    return await response.json();
  } else if (response.status === 204) {
    return true;
  } else {
    const data = await response?.json();
    return { error: true, message: data?.message };
  }
};

export const postRequest = async (url, data) => sendRequest(url, data, "POST");

export const putRequest = async (url, data) => sendRequest(url, data, "PUT");

// export const getRequest = async (url) => sendRequest(url, "get");

export const getRequest = async (url) => {
  const response = await fetch(url, {
    method: "GET",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      Cache: "no-cache",
    },
  });

  if (response.status === 200) {
    return await response.json();
  } else {
    const data = await response.json();

    return { error: true, message: data.message };
  }
};
