// LocationGrid.js
import React, { useState, useEffect } from "react";
import "./LocationGrid.css";
import Map from "./Maps";
import LocationTable from "./LocationTable";
import { postRequest, putRequest, getRequest } from "./helper/ApiRequests";

import ModalForm from "./ModalForm";

const LocationGrid = () => {
  // Sample data for multiple rows

  // const [rows, setRows] = useState([]);

  const [locations, setLocations] = useState([
    // { name: "Location 1", notes: "Notes 1", lat: 48.86, lng: 2.3322 },
    // { name: "Location 2", notes: "Notes 2", lat: 48.85, lng: 2.3322 },
    // { name: "Location 3", notes: "Notes 3", lat: 48.855, lng: 2.34 },
  ]);

  const [isFormOpen, setIsFormOpen] = useState(false);

  useEffect(() => {
    (async () => {
      const data = await getRequest("http://192.168.1.10:7147/api/Location");
      setLocations(data);
    })();
  }, [locations]);
  const toggleForm = () => {
    setIsFormOpen(!isFormOpen);
  };
  const handleAddLocation = async (newLocation) => {
    await postRequest(newLocation);
    setLocations([...locations, newLocation]);
    setIsFormOpen(false); // Close the form after adding a location
  };
